import React, {useEffect, useState} from 'react';

import BackendErrorMessages from 'components/backend-error-messages';

const CommentForm = ({onSubmit, errors, initialValues, authorImage}) => {
    const [body, setBody] = useState('');

    const handleSubmit = event => {
        event.preventDefault();

        const comment = {
            body
        };

        onSubmit(comment);
    };

    useEffect(() => {
        if (!initialValues) {
            return
        }
        setBody(initialValues.body);
    }, [initialValues]);

    console.log('commenting', body);
    return (
        <div className="row">
            <div className="col-md-8 offset-md-2 col-xs-12">
                {errors && <BackendErrorMessages backendErrors={errors} />}
                <form
                    onSubmit={handleSubmit}
                    className="card comment-form"
                >
                    <div className="card-block">
                        <textarea
                            className="form-control"
                            rows="3"
                            placeholder="Write a comment..."
                            value={body}
                            onChange={e => setBody(e.target.value)}
                        />
                    </div>
                    <div className="card-footer">
                        {/*<img*/}
                        {/*    src={authorImage}*/}
                        {/*    className="comment-author-img"*/}
                        {/*    alt="" */}
                        {/*/>*/}
                        <button
                            type="submit"
                            className="btn btn-sm pull-xs-right btn-primary">
                            Post Comment
                        </button>
                    </div>
                </form>
            </div>
        </div>
    )
};

export default CommentForm;