import React, {useEffect, useContext} from 'react';
import {NavLink} from 'react-router-dom';

import useFetch from 'hooks/use-fetch';
import UserArticles from 'pages/user-profile/components/user-articles';
import AddToFollow from 'components/add-to-follow';
import {CurrentUserContext} from 'contexts/current-user';

const UserProfile = ({location, match}) => {
    const [currentUserState] = useContext(CurrentUserContext);
    const slug = match.params.slug;
    const isFavorites = location.pathname.includes('favorites');
    const isFollowing = location.pathname.includes('following');
    const apiUrl = `/profiles/${slug}`;
    // console.log('ff', slug, isFavorites, isFollowing);
    const [{response}, doFetch] = useFetch(apiUrl);

    useEffect(() => {
        doFetch();
    }, [doFetch]);

    if (!response) {
        return null;
    }

    return (
        <div className="profile-page">
            <div className="user-info">
                <div className="container">
                    <div className="row">
                        <div className="col-xs-12 col-md-10 offset-md-1">
                            <img className="user-img" src={response.profile.image} alt="" />
                            <h4>{response.profile.username}</h4>
                            <p>{response.profile.bio}</p>
                            {currentUserState.currentUser.username !== response.profile.username &&
                            <AddToFollow
                                username={response.profile.username}
                                isFollowing={isFollowing}
                                userSlug = {match.url}
                            />
                            }
                        </div>
                    </div>
                </div>
            </div>
            <div className="container">
                <div className="row">
                    <div className="col-xs-12 col-md-10 offset-md-1">
                        <div className="article-toggle">
                            <ul className="nav nav-pills outline-active">
                                <li className="nav-item">
                                    <NavLink
                                        to={`/profiles/${response.profile.username}`}
                                        className="nav-link"
                                        exact >
                                        My Posts
                                    </NavLink>
                                </li>
                                <li className="nav-item">
                                    <NavLink
                                        to={`/profiles/${response.profile.username}/favorites`}
                                        className="nav-link" >
                                        Favorites Posts
                                    </NavLink>
                                </li>
                            </ul>
                        </div>
                        <UserArticles
                            username={response.profile.username}
                            location={location}
                            isFavorites={isFavorites}
                            url={match.url} />
                    </div>
                </div>
            </div>
        </div>
    )
};

export default UserProfile;